$(document).ready(function(){
    $(".btn").click(function(){
        $(".lorem").slideDown("slow");
    })
    
})
// ============================================slideUp========================================
$(document).ready(function(){
    $(".btn").click(function(){
        $(".lorem").slideUp("slow");
    })
    
})
// ============================================slideToggle========================================
$(document).ready(function(){
    $(".btn").click(function(){
        $(".lorem").slideToggle("slow");
    })
    
})